green_cells = [
    '1C', '1D', '2B', '2C', '3B', '3E', '3F', '4B', '4F', '5B', '5F',
    '6B', '6F', '6G', '7B', '7F', '7G', '8B', '8C', '8F', '8G', '9C',
    '9F', '9G', '10C', '10F', '10G', '11C', '11E', '11F', '11G', '12C',
    '12E', '12F', '13B', '13C', '13E', '13F', '14B', '14E', '14F', '15B',
    '15E', '15F', '16B', '16C', '16D', '16E', '16F'
]

# Convert cell indices to coordinates
coordinates = [(int(cell[:-1]), ord(cell[-1]) - ord('A') + 1) for cell in green_cells]

# Create adjacency list for the graph
adjacency_list = {coord: [] for coord in coordinates}

# Define possible moves (up, down, left, right)
moves = [(-1, 0), (1, 0), (0, -1), (0, 1)]

# Populate adjacency list
for coord in coordinates:
    for move in moves:
        neighbor = (coord[0] + move[0], coord[1] + move[1])
        if neighbor in coordinates:
            adjacency_list[coord].append(neighbor)

# Function to check if a Hamiltonian cycle exists
def is_hamiltonian_cycle(start, path, visited):
    if len(path) == len(coordinates):
        return path[0] in adjacency_list[path[-1]]  # Check if it forms a cycle
    for neighbor in adjacency_list[start]:
        if neighbor not in visited:
            visited.add(neighbor)
            path.append(neighbor)
            if is_hamiltonian_cycle(neighbor, path, visited):
                return True
            path.pop()
            visited.remove(neighbor)
    return False

# Check for Hamiltonian cycle starting from each node
for start in coordinates:
    if is_hamiltonian_cycle(start, [start], {start}):
        print(f"Hamiltonian cycle exists starting at {start}")
        break
else:
    print("No Hamiltonian cycle exists")

# Define the operation table
